from datetime import datetime
from zoneinfo import ZoneInfo

# Récupérer les données combinées depuis le Merge
# IMPORTANT: Utiliser _item (avec underscore) en Python n8n
merged_data = _item['json']

# Extraire les données du stock (venant de PostgreSQL)
stock_info = {
    'id': merged_data.get('id'),
    'ticker': merged_data.get('ticker'),
    'name': merged_data.get('name')
}

# Les données Yahoo Finance sont aussi dans merged_data
yahoo_response = merged_data

try:
    # Vérifier si on a les données Yahoo Finance
    if yahoo_response.get('chart') and yahoo_response['chart'].get('result') and len(yahoo_response['chart']['result']) > 0:
        chart = yahoo_response['chart']['result'][0]
        quote = chart['indicators']['quote'][0]
        timestamp = chart['timestamp'][0]

        # Convertir timestamp en date (timezone Europe/Paris)
        date = datetime.fromtimestamp(timestamp, tz=ZoneInfo('Europe/Paris')).strftime('%Y-%m-%d')

        # Récupérer adjusted close si disponible
        adj_close_data = chart['indicators'].get('adjclose')
        if adj_close_data and len(adj_close_data) > 0 and adj_close_data[0].get('adjclose'):
            adjusted_close = adj_close_data[0]['adjclose'][0]
        else:
            adjusted_close = quote['close'][0]

        return {
            'json': {
                'stock_id': stock_info['id'],
                'ticker': stock_info['ticker'],
                'name': stock_info['name'],
                'date': date,
                'open': quote['open'][0],
                'high': quote['high'][0],
                'low': quote['low'][0],
                'close': quote['close'][0],
                'volume': quote['volume'][0],
                'adjusted_close': adjusted_close,
                'success': True
            }
        }
    else:
        return {
            'json': {
                'stock_id': stock_info['id'],
                'ticker': stock_info['ticker'],
                'name': stock_info['name'],
                'success': False,
                'error': 'No data in response'
            }
        }
except Exception as error:
    return {
        'json': {
            'stock_id': stock_info.get('id') if 'stock_info' in locals() else None,
            'ticker': stock_info.get('ticker', 'error') if 'stock_info' in locals() else 'error',
            'name': stock_info.get('name', 'error') if 'stock_info' in locals() else 'error',
            'success': False,
            'error': str(error)
        }
    }

