// Parser la réponse Yahoo Finance
const yahooResponse = $input.item.json;

// Récupérer les infos de stock depuis les données passées (pairedItem)
const stockInfo = $node['Récupérer les actions'].json[$input.item.pairedItem];

try {
  if (yahooResponse.chart && yahooResponse.chart.result && yahooResponse.chart.result[0]) {
    const chart = yahooResponse.chart.result[0];
    const quote = chart.indicators.quote[0];
    const timestamp = chart.timestamp[0];

    // Convertir timestamp en date
    const date = new Date(timestamp * 1000).toISOString().split('T')[0];

    return {
      json: {
        stock_id: stockInfo.id,
        ticker: stockInfo.ticker,
        name: stockInfo.name,
        date: date,
        open: quote.open[0],
        high: quote.high[0],
        low: quote.low[0],
        close: quote.close[0],
        volume: quote.volume[0],
        adjusted_close: chart.indicators.adjclose ? chart.indicators.adjclose[0].adjclose[0] : quote.close[0],
        success: true
      }
    };
  } else {
    return {
      json: {
        stock_id: stockInfo.id,
        ticker: stockInfo.ticker,
        name: stockInfo.name,
        success: false,
        error: 'No data in response'
      }
    };
  }
} catch (error) {
  return {
    json: {
      stock_id: stockInfo ? stockInfo.id : null,
      ticker: stockInfo ? stockInfo.ticker : 'unknown',
      name: stockInfo ? stockInfo.name : 'unknown',
      success: false,
      error: error.message
    }
  };
}

